import React from "react";
import { Row, Col, Card, CardTitle } from "react-materialize";

export default class BookInformation extends React.Component {
  constructor(props) {
    super(props);
    const volumeInfo = this.props.volume.volumeInfo;
    this.state = {
      image: volumeInfo.imageLinks.thumbnail,
      title: volumeInfo.title,
      authors: volumeInfo.authors,
      description: volumeInfo.description,
      link: volumeInfo.infoLink
    };
  }

  render() {
    return (
      <Row>
        <Col m={1} />
        <Col m={10} s={12} center-align>
          <Card
            horizontal
            header={<CardTitle image={this.state.image} />}
            actions={[<a />]}
          >
            <h6>{this.state.title}</h6>
            {this.state.authors.length !== 1 ? (
              <p> Authors:  
              {this.state.authors.map((author, index) => {
                if(index === this.state.authors.length - 1) {

                }
                return author + ', ';
              })}
              </p>
            ) : (
              <p>Author: {this.state.authors[0]}</p>
            )}
            <p>{this.state.description}</p>
          </Card>
        </Col>
      </Row>
    );
  }
}
