import React from "react";
import { Row, Col, Card, TextInput, Button, Icon } from "react-materialize";
import {
  AxiosProvider,
  Request,
  Get,
  Delete,
  Head,
  Post,
  Put,
  Patch,
  withAxios
} from "react-axios";
import Axios from "axios";



const requestBooks = () => {
  const bookName = this.state.textInput
  Axios.get(
    `https://www.googleapis.com/books/v1/volumes?q=${bookName}&key=AIzaSyCWImxMTM8vjEFwzjjDfTH--sXV1Rz96u4`
  )
    .then(function(response) {
      console.log(response.data.items);


    })
    .catch(function(error) {
      // handle error
      console.log(error);
    });
};

class SeachBox extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      textInput: ""
    };
  }


  handleChange = name => event => {
    this.setState({
      [name]: event.target.value
    });
  };

  render() {
    return (
      <div id="container">
        <Row>
          <Col m={12} s={12}>
            <Card
              className="blue-grey darken-1"
              textClassName="white-text"
              title="Book Search"
            >
              <Row>
                <p>Enter a book you would like to search</p>
                <TextInput
                  placeholder="Book"
                  value={this.state.textInput}
                  id="bookInput"
                  onChange={this.handleChange("textInput")}
                />
                <Button
                  type="submit"
                  waves="light"
                  onClick={() => this.requestBooks()}
                >
                  Submit
                  <Icon right>send</Icon>
                </Button>
              </Row>
            </Card>
          </Col>
        </Row>
        {this.requestBooks()}
      </div>
    );
  }
}

export default SeachBox;
