import React from "react";
import { Row, Col, Card, CardTitle } from "react-materialize";


export default class BookInformation extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        image: this.props.volume.volumeInfo.imageLinks.thumbnail
    };
  }

  render() {
    return (
      <Row>
        <Col m={6} s={12}>
          <Card horizontal header={this.state.image} actions={[<a />]}>
            Here is the standard card with a horizontal image.
          </Card>
        </Col>
      </Row>
    );
  }
}
