import React from "react";
import axios from "axios";
import { Row, Col, Card, CardTitle } from "react-materialize";

export default class Saved extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      booksReady: false,
      booksSaved: []
    };
  }

  componentDidMount() {
    this.props.savedBooks.map(bookId => {
      axios
        .get("https://www.googleapis.com/books/v1/volumes/" + bookId)
        .then(res => {
          const tempBooks = this.state.booksSaved;
          tempBooks.push(res);
          this.setState({
            booksSaved: tempBooks
          });
        })
        .catch(err => {
          if (err) throw err;
        });
    });
    this.setState({
      booksReady: true
    });
  }

  getBookInformation = id => {
    return axios
      .get("https://www.googleapis.com/books/v1/volumes/" + id)
      .then(res => {
        console.log(res);
        return <p key={res.data.id}>{res.data.volumeInfo.title}</p>;
      })
      .catch(err => {
        if (err) throw err;
      });
  };

  render() {
    return (
      <>
        {this.state.booksReady ? (
          <Row>
            <Col m={1} />
            <Col m={10} s={12} center-align>
              <Card
                horizontal
                header={<CardTitle image={this.state.image} />}
                actions={[<a />]}
              >
                <Button onClick={this.gatherInformationFromBook}>
                  {" "}
                  Save Book
                </Button>
                <h6>{this.state.title}</h6>
                {this.state.authors.length !== 1 ? (
                  <p>
                    {" "}
                    <span style={{ fontWeight: "bold" }}>Authors: </span>
                    {this.state.authors.map((author, index) => {
                      if (index === this.state.authors.length - 1) {
                        return author;
                      }
                      return author + ", ";
                    })}
                  </p>
                ) : (
                  <p>
                    <span style={{ fontWeight: "bold" }}>Author: </span>
                    {this.state.authors[0]}
                  </p>
                )}
                <p>{this.state.description}</p>

                <a href={this.state.link}>More Information</a>
              </Card>
            </Col>
          </Row>
        ) : (
          console.log("books not ready")
        )}
      </>
    );
  }
}
