import React, { Component } from "react";
import "./App.css";
import Nav from "./components/Nav/Nav";
import Header from "./components/Header/Header";
import SearchBox from "./components/SearchBox/SearchBox";
import BookInformation from "../src/components/BookInformation/BookInformation";
import Axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      booksSet: false
    };
  }

  axiosBookRequest = bookName => {
    Axios.get(
      `https://www.googleapis.com/books/v1/volumes?q=${bookName}&key=AIzaSyCWImxMTM8vjEFwzjjDfTH--sXV1Rz96u4`
    )
      .then(response => {
        console.log(response.data.items);
        this.setState({
          books: response.data.items,
          booksSet: true
        });
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  };

  render() {
    return (
      <div id="container">
        <Nav />
        <Header />
        <SearchBox requestBooks={this.axiosBookRequest} />
        {this.state.booksSet ? (
          this.state.books.map(book => {
            return <BookInformation volume={book} />;
          })
        ) : (
          <div>IM also tired</div>
        )}
      </div>
    );
  }
}

export default App;
