import React from "react";
import { Row, Col, Card, TextInput, Button, Icon } from "react-materialize";
import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'

class SeachBox extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      textInput: ''
    };
  }

  requestBooks = () => {

  }

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value
    })
  }

  render() {
    return (
      <Row>
        <Col m={12} s={12}>
          <Card
            className="blue-grey darken-1"
            textClassName="white-text"
            title="Book Search"
          >
              <Row>
              <p>Enter a book you would like to search</p>
            <TextInput placeholder="Book" value={this.state.textInput} id='bookInput' onChange={this.handleChange('textInput')}/>
            <Button type="submit" waves="light" onClick={() => alert(this.state.textInput)}>
              Submit
              <Icon right>send</Icon>
            </Button>
              </Row>
          </Card>
        </Col>
      </Row>

    );
  }
}

export default SeachBox;
