import React from 'react'
import axios from 'axios'

export default class Saved extends React.Component {
    constructor(props) {
        super(props);
        this.state= {}
    }





    render() {
        return (
            <>
            {this.props.savedBooks.map(bookId => {
                return         axios.get('https://www.googleapis.com/books/v1/volumes/' + bookId)
                .then(res => {
                    console.log(res.data.volumeInfo.title)
                    return <p>{res.data.volumeInfo.title}</p>
                })
                .catch(err => {
                    if (err) throw err;
                })
            })}
            </>
        )
    }
}
